import React from 'react';
/**
 * Author recommends to use this library in the form of a ponyfill, which doesn't inflict modifications of the global object.
 * Ponyfill explanation - https://github.com/sindresorhus/ponyfill
 */
import ResizeObserverPonyfill from 'resize-observer-polyfill';
export function useResizeObserver(callback) {
    var _a = React.useState(null), el = _a[0], setEl = _a[1];
    React.useEffect(function () {
        if (!el || !callback) {
            return;
        }
        var resizeObserver = new ResizeObserverPonyfill(callback);
        resizeObserver.observe(el);
        return function () {
            resizeObserver.disconnect();
        };
    }, [el, callback]);
    return setEl;
}
