import React from 'react';
import { isIE11 } from '../utils/isIE11';
export function useScrollLock(shouldScrollLock) {
    React.useEffect(function () {
        var IE11 = isIE11();
        if (!IE11 && shouldScrollLock) {
            var bodyPosition_1 = document.body.style.position;
            var bodyTop_1 = document.body.style.top;
            document.body.style.top = "-".concat(window.scrollY, "px");
            document.body.style.position = 'fixed';
            return function () {
                var scrollY = document.body.style.top;
                document.body.style.position = bodyPosition_1;
                document.body.style.top = bodyTop_1;
                window.scrollTo(0, parseInt(scrollY || '0') * -1);
            };
        }
    }, [shouldScrollLock]);
}
