import { Props } from '../../utils/types';
import { MenuRef, Selection } from '../MenuImperative/MenuImperative.types';
export interface FieldProps extends Props {
    label?: string;
}
export interface PageNotationProps extends Props {
    end: number;
    items: number;
    start: number;
}
export interface NavigationIconProps extends Props {
    disabled?: boolean;
    onClick?: any;
}
export interface PresetPaginationProps extends Props {
    /**
     * Current page selected, 1 based.
     */
    activePage?: number;
    /**
     * Total number of items across pages.
     */
    items: number;
    /**
     * A callback that receives the page selected.
     */
    onSelectPage?: (page: number) => void;
    /**
     * Number of items per page.
     */
    perPage?: number;
    /**
     * @deprecated
     *
     * The `fieldLabel` has been deprecated and will be removed in a future version.
     * The `Pagination` component now handles i18n internally and this prop has no effect,
     * please remove it from usage.
     *
     *
     * **Before**
     *
     * `<Pagination fieldLabel={window.i18n.t('my.key')} />`
     *
     * **After**
     *
     * `<Pagination />`
     *
     * @deprecatedSince 10.20.0
     */
    fieldLabel?: string;
}
export interface PaginationMenuProps {
    activePage: number;
    menuRef: React.RefObject<MenuRef>;
    pages: number;
    onSelect?: PageSelectProps['onSelect'];
}
export interface PageSelectProps extends Props {
    activePage?: number;
    disabled?: boolean;
    onSelect?: (selection: Selection) => any;
    pages?: number;
}
export interface PaginationHookConfig {
    activePage: number;
    items: number;
    onSelectPage: (page: number) => void;
    perPage: number;
}
export interface PaginationHook {
    end: number;
    isFirst: boolean;
    isLast: boolean;
    pages: number;
    selectNext: () => void;
    selectPage: (selection: Selection) => void;
    selectPrev: () => void;
    start: number;
}
