import { InputAttributes } from '../../utils/types';
export interface SliderProps extends InputAttributes {
    /**
     * Whether or not to put the slider into a disabled state.
     */
    disabled?: boolean;
    /**
     * The maximum value of the slider (right side)
     * @defaultValue 100
     */
    max?: number;
    /**
     * The minimum value of the slider (left side)
     * @defaultValue 0
     */
    min?: number;
    /**
     * Whether or not to show value labels below the slider increments.
     */
    showLabel?: boolean;
    /**
     * The distance between each stopping point on the Slider.
     * @defaultValue 1
     */
    step?: number;
    /**
     * The currently set value of the Slider.
     * Defaults to the middle between min and max.
     * @defaultValue (max < min) ? min : min + (max - min)/2;
     */
    value?: number;
}
