export interface OverflowState {
    ref: (el: HTMLElement | null) => void;
    /**
     * @description Is overflowing along X or Y axis (horizontally or vertically)
     */
    isOverflowing: boolean;
    /**
     * @description Is overflowing along X axis (horizontally)
     */
    isOverflowingX: boolean;
    /**
     * @description Is overflowing along Y axis (vertically)
     */
    isOverflowingY: boolean;
}
export interface OverflowObserverProps {
    children(state: OverflowState): JSX.Element;
}
