import { BoxProps, MarginSize } from '../Box/Box.types';
import { FlexProps } from '../Flex/Flex.types';
export interface FlexListProps extends FlexProps {
    /**
     * Props to apply to each Box that wraps each child.
     */
    boxProps?: BoxProps;
    /**
     * Margin on a single item. Between two items, `size` is double the distance of `space`.
     */
    size?: MarginSize;
    /**
     * Total margin between items. Between two items, `space` is half the distance of `size`.
     */
    space?: MarginSize;
}
